import React from 'react';
import { CssBaseline, Hidden, Toolbar } from '@material-ui/core';
import {
  Build as BuildIcon,
  Email as EmailIcon,
  EmojiEvents as EmojiEventsIcon,
  GetApp as GetAppIcon,
  InsertEmoticon as InsertEmoticonIcon,
  Menu as MenuIcon,
  Work as WorkIcon
} from '@material-ui/icons';
import NavAppBar from './AppBar';
import NavDrawer from './Drawer';
import NavDrawerContainer from './DrawerContainer';
import NavListItemGutter from './ListItemGutter';
import NavListItem from './ListItem';
import NavListItemIcon from './ListItemIcon';
import NavListItemText from './ListItemText';
import NavIconButton from './IconButton';

const NavBarIcons = {
  BuildIcon,
  EmailIcon,
  EmojiEventsIcon,
  GetAppIcon,
  InsertEmoticonIcon,
  WorkIcon
};

export default function NavBar({ getter, handler }) {
  console.log('a');
  return (
    <React.Fragment>
      <CssBaseline />

      <NavDrawerContainer>
        <NavDrawer variant="permanent" anchor="left">
          {[].map(({ path, icon, label }, index) => {
            const Icon = NavBarIcons[icon];

            return (
              <React.Fragment key={`navbar-item-${index}`}>
                <NavListItem
                  button
                  className={`${getter('isActivePage')(path) ? 'active' : ''}`}
                  onClick={() => handler('navToPage')(path)}
                >
                  <NavListItemGutter />
                  <NavListItemIcon>
                    <Icon fontSize="large" />
                  </NavListItemIcon>
                  <NavListItemText primary={label} />
                  <NavListItemGutter />
                </NavListItem>
              </React.Fragment>
            );
          })}
        </NavDrawer>
      </NavDrawerContainer>
    </React.Fragment>
  );
}
