import React from 'react';
import { CssBaseline, Collapse, List } from '@material-ui/core';
import {
  Build as BuildIcon,
  Email as EmailIcon,
  EmojiEvents as EmojiEventsIcon,
  GetApp as GetAppIcon,
  InsertEmoticon as InsertEmoticonIcon,
  Work as WorkIcon
} from '@material-ui/icons';
import NavDrawer from './Drawer';
import NavDrawerContainer from './DrawerContainer';
import NavListItemGutter from './ListItemGutter';
import NavListItem from './ListItem';
import NavListItemIcon from './ListItemIcon';
import NavListItemText from './ListItemText';

const NavBarIcons = {
  BuildIcon,
  EmailIcon,
  EmojiEventsIcon,
  GetAppIcon,
  InsertEmoticonIcon,
  WorkIcon
};

const SingleNavBarItem = ({
  StyledLink,
  StyledListItem,
  StyledListItemIcon,
  StyledListItemText,
  bIsActive,
  icon,
  label,
  path
}) => {
  const Icon = Icons[icon];

  return (
    <StyledLink to={path}>
      <StyledListItem button className={`${bIsActive ? 'active' : ''}`}>
        <StyledListItemIcon>
          <Icon />
        </StyledListItemIcon>
        <StyledListItemText primary={label} />
      </StyledListItem>
    </StyledLink>
  );
};

const DropDownNavBarItem = ({
  StyledLink,
  StyledListItem,
  StyledListItemIcon,
  StyledListItemText,
  bIsActive,
  icon,
  label,
  navBarItemChildren
}) => {
  const [bIsOpen, setOpen] = useState(false);
  const Icon = Icons[icon];

  return (
    <div onClick={() => setOpen(!bIsOpen)}>
      <StyledListItem button className={`${bIsActive ? 'active' : ''}`}>
        <StyledListItemIcon>
          <Icon />
        </StyledListItemIcon>
        <StyledListItemText primary={label} />
        <StyledListItemIcon>
          {bIsActive || bIsOpen ? <ExpandLessIcon /> : <ExpandMoreIcon />}
        </StyledListItemIcon>
      </StyledListItem>

      <Collapse in={bIsActive || bIsOpen} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          {navBarItemChildren.map(({ label, bIsActive, path }, index) => (
            <React.Fragment key={`navbar-item-collapsible-${index}`}>
              <StyledLink to={path}>
                <StyledListItem
                  button
                  className={`${bIsActive ? 'active' : ''}`}
                >
                  <StyledListItemText primary={label} />
                </StyledListItem>
              </StyledLink>
            </React.Fragment>
          ))}
        </List>
      </Collapse>
    </div>
  );
};

export default function NavBar({ getter, handler }) {
  return (
    <React.Fragment>
      <CssBaseline />
      <NavDrawerContainer>
        <NavDrawer variant="permanent" anchor="left">
          <List>
            {navBarItems.map((navBarItem, index) => {
              const {
                label,
                icon,
                bIsActive,
                path,
                navBarItemChildren
              } = navBarItem;

              return (
                <React.Fragment key={`navbar-item-${index}`}>
                  {navBarItemChildren ? (
                    <DropDownNavBarItem
                      {...{
                        StyledLink,
                        StyledListItem,
                        StyledListItemIcon,
                        StyledListItemText,
                        bIsActive,
                        icon,
                        label,
                        navBarItemChildren
                      }}
                    />
                  ) : (
                    <SingleNavBarItem
                      {...{
                        StyledLink,
                        StyledListItem,
                        StyledListItemIcon,
                        StyledListItemText,
                        bIsActive,
                        icon,
                        label,
                        path
                      }}
                    />
                  )}
                </React.Fragment>
              );
            })}
          </List>
        </NavDrawer>
      </NavDrawerContainer>
    </React.Fragment>
  );
}
